package ExerciciosFeriado;

public class MediaAritimetica {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int numero1 = 8, numero2 = 9, numero3 = 7, numero4 = 4, numero5 = 5, numero6 = 6, media1, media2, mediaTotal;
		
		media1 = (numero1 + numero2 + numero3) / 3;
		media2 = (numero4 + numero5 + numero6) / 3;
		mediaTotal = (media1 + media2) / 2;
		
		System.out.println("A primeira media é: " + media1);
		System.out.println("A segunda media é: " + media2);
		System.out.println("A media da Media é: " + mediaTotal);
		
	}

}
